/* isql OpenQwaqActivityLog UserId UserPass -b < OpenQwaqActivityLog.sql  */
/* NOTE: isql is pathetic; everything must be single line, sorry! */
CREATE TABLE sp_activity_log (id INTEGER UNSIGNED NOT NULL DEFAULT NULL AUTO_INCREMENT, logdate DATETIME NOT NULL, host VARCHAR(100) NOT NULL, action VARCHAR(100) NOT NULL, user VARCHAR(100) NOT NULL, org VARCHAR(100) NOT NULL, extra VARCHAR(1000) NOT NULL, PRIMARY KEY(id)) Engine=InnoDB;
CREATE TABLE  client_activity_data_log (client_activity_data_log_id bigint(20) unsigned NOT NULL auto_increment, datetime_created timestamp NOT NULL default CURRENT_TIMESTAMP, client_version varchar(50) NOT NULL, realm_name varchar(255) NOT NULL, username varchar(100) NOT NULL, status tinyint(3) NOT NULL default '0' COMMENT '1 = processed, 0 = unprocessed, -1 = error, defaults to false', activity_data longtext NOT NULL, PRIMARY KEY (client_activity_data_log_id)) ENGINE=InnoDB;
CREATE TABLE  client_performance_data_log (client_performance_data_log_id bigint(20) unsigned NOT NULL auto_increment, datetime_created timestamp NOT NULL default CURRENT_TIMESTAMP, client_version varchar(50) NOT NULL, realm_name varchar(255) NOT NULL, username varchar(100) NOT NULL, status tinyint(3) NOT NULL default '0' COMMENT '1 = processed, 0 = unprocessed, -1 = error, defaults to false', performance_data longtext NOT NULL, PRIMARY KEY  (client_performance_data_log_id)) ENGINE=InnoDB;
CREATE TABLE  client_system_information_log (client_system_information_log_id bigint(20) unsigned NOT NULL auto_increment, datetime_created timestamp NOT NULL default CURRENT_TIMESTAMP, client_version varchar(50) NOT NULL, realm_name varchar(255) NOT NULL, username varchar(100) NOT NULL, status tinyint(3) NOT NULL default '0' COMMENT '1 = processed, 0 = unprocessed, -1 = error, defaults to false', system_information longtext NOT NULL, PRIMARY KEY  (client_system_information_log_id)) ENGINE=InnoDB;
