'From Croquet1.0beta of 11 April 2006 [latest update: #1] on 31 October 2009 at 11:18:45 am'!!QSlaveProjectionScreen methodsFor: 'accessing' stamp: 'bgf 10/19/2009 20:24'!photographerClass
	^ QDirectPhotoJob! !!QMultiBoard methodsFor: 'menu' stamp: 'bgf 10/28/2009 15:57'!checkRemoveTab: anEvent	|rmMenu |	self numTabs > 1 ifFalse: [		anEvent avatar signal: #alertMessage with: 'Cannot remove the last tab.'.		^ self.	].	(self notes size = 0) 	ifTrue: [		^ self removeCurrentTab	].	rmMenu := TMenuFrame new.	(rmMenu addItemSpec: { 'Remove ', (self currentTabName), '?' . #cancelMenu: } target: self) 			 disabled: true.	(self notes size > 0) ifTrue: [		| msg |		msg := ((self notes size > 1)				ifTrue: [ '', (self notes size), ' items will be deleted.' ]				ifFalse: [ 'One item will be deleted.' ]).		(rmMenu addItemSpec: { msg .  #cancelMenu:} 				 target: self) 				disabled: true.	].	rmMenu addList: {			#- .			{'Yes' . #removeCurrentTab}.			{'No' . #cancelMenu:}.		} target: self.	self postMenu: rmMenu forEvent: anEvent! !!QMultiBoard methodsFor: 'menu' stamp: 'bgf 10/28/2009 15:45'!tradeTabBackward: index	"Swap the given tab with the one just before it in the list."	| tmp |	(index < 2 or: [ index > self numTabs]) ifTrue: [ 		"The button sensitivities are guarded, but removals may overlap a fire."		^ self ].	tmp := tabs at: index.	tabs at: index put: (tabs at: index-1).	tabs at: index-1 put: tmp.	tmp := tabNames at: index.	tabNames at: index put: (tabNames at: index-1).	tabNames at: index-1 put: tmp.	index = cursor ifTrue: [		self cursor: index-1	].! !!QMultiBoard methodsFor: 'menu' stamp: 'bgf 10/28/2009 15:44'!tradeTabForward: index	"Swap the given tab with the one just after it in the list."	index >= self numTabs ifTrue: [ 		"The button sensitivities are guarded, but removals may overlap a fire."		^ self ].	self tradeTabBackward: index+1.	index = cursor ifTrue: [		self cursor: index+1	].! !!QMultiBoard methodsFor: 'private' stamp: 'bgf 10/28/2009 15:46'!removeCurrentTab	self numTabs > 1 ifTrue: [		self removeTab: (self cursor).	].! !